def get_cipher(cipher_stats, cipher_types):
    z_score_list = []
    for i in range(len(cipher_types)):
        z_score = 0.0
        for j in range(9):
            standard_deviation = std[j][i]
            if j == 0:
                standard_deviation += 0.001
            if average[j][i] == 0:
                z_score += cipher_stats[j]
            else:
                z_score += abs((cipher_stats[j] - average[j][i]) / standard_deviation)
        z_score_list.append([cipher_types[i], z_score])
    return z_score_list
